import {
	HStack,
	Input,
	Modal,
	ModalBody,
	ModalContent,
	ModalFooter,
	ModalOverlay,
	useDisclosure,
	VStack,
	Text,
	Button,
} from "@chakra-ui/react";

import React, { useState } from "react";

const HabitItem = ({ title, description, startDate, endDate, id }) => {
	const [habit, setHabit] = useState({
		title: "",
		description: "",
		startDate: "",
		endDate: "",
	});
	const { isOpen, onOpen, onClose } = useDisclosure();

	const onChange = (e) => {
		setHabit((prevState) => {
			return {
				...prevState,
				[e.target.id]: e.target.value,
			};
		});
	};

	return (
		<Modal isOpen={isOpen}>
			<ModalOverlay />
			<ModalContent>
				<ModalBody p="10">
					<VStack spacing="4" alignItems="start">
						<Input
							placeholder="Title"
							id="title"
							value={habit.title}
							onChange={onChange}
						/>

						<Input
							placeholder="Description"
							id="description"
							value={habit.description}
							onChange={onChange}
						/>

						<VStack spacing="1" w="100%" alignItems="start">
							<Text fontSize="sm">Start Date</Text>
							<Input
								type="date"
								placeholder="Start Date"
								id="startDate"
								value={habit.startDate}
								onChange={onChange}
							/>
						</VStack>
						<VStack spacing="1" w="100%" alignItems="start">
							<Text fontSize="sm">End Date</Text>
							<Input
								type="date"
								placeholder="End Date"
								id="endDate"
								value={habit.endDate}
								onChange={onChange}
							/>
						</VStack>
					</VStack>
				</ModalBody>
				<ModalFooter>
					<HStack spacing="5">
						<Button p="2" fontSize="sm" onClick={onClose}>
							Cancel
						</Button>
						<Button onClick={null} p="2" fontSize="sm">
							Add Habit
						</Button>
					</HStack>
				</ModalFooter>
			</ModalContent>
		</Modal>
	);
};

export default HabitItem;
