import {
	HStack,
	Input,
	Modal,
	ModalBody,
	ModalContent,
	ModalFooter,
	ModalOverlay,
	useDisclosure,
	VStack,
	Text,
	Button,
} from "@chakra-ui/react";

import React, { useState } from "react";

const HabitItem = ({ title, description, startDate, endDate, id }) => {
	const [habit, setHabit] = useState({
		title: "",
		description: "",
		startDate: "",
		endDate: "",
	});
	const { isOpen, onOpen, onClose } = useDisclosure();

	const onChange = (e) => {
		setHabit((prevState) => {
			return {
				...prevState,
				[e.target.id]: e.target.value,
			};
		});
	};

	return null;
};

export default HabitItem;
