import {
	Box,
	Button,
	Flex,
	HStack,
	Input,
	Modal,
	ModalBody,
	ModalContent,
	ModalFooter,
	ModalOverlay,
	Text,
	useDisclosure,
	VStack,
} from "@chakra-ui/react";
import HabitItem from "./HabitItem";
import React, { useState } from "react";

const Habits = () => {
	const [habitCount, setHabitCount] = useState(0);

	const [habits, setHabits] = useState([]);
	const [modalState, setModalState] = useState(false);

	const onChange = (e) => {
		setHabits((prevState) => {
			return {
				...prevState,
				[e.target.id]: e.target.value,
			};
		});
	};
	const onAddHabit = () => {
		setDisplayHabit(true);
		setHabitCount(habitCount + 1);
	};

	const changeModalState = () => {
		setModalState(!modalState);
	};

	return (
		<Box m="5">
			<Flex justifyContent="space-between" alignItems="center">
				<Text fontSize="3xl">Habits</Text>
				<Button onClick={changeModalState}>Add Habit</Button>
			</Flex>

			<Text fontSize="sm">
				{habitCount} active habit{habitCount > 1 && "'s"}
			</Text>
			<hr
				style={{
					marginTop: "5px",
					marginBottom: "25px",
					height: "1px",
					border: "none",
					backgroundColor: "#1A202C",
				}}
			/>
			{/* {displayHabit && <Text>{habits.startDate}</Text>} */}
			<HabitItem open={modalState} />
		</Box>
	);
};

export default Habits;
