import { Text } from "@chakra-ui/react";

import React, { useState } from "react";

const HabitItem = ({ isOpen }) => {
	const [habit, setHabit] = useState({
		title: "",
		description: "",
		startDate: "",
		endDate: "",
	});

	const { onClose } = useDisclosure();

	const onChange = (e) => {
		setHabit((prevState) => {
			return {
				...prevState,
				[e.target.id]: e.target.value,
			};
		});
	};

	const updateModal = () => {
		onClose();
	};

	return <Text></Text>;
};

export default HabitItem;
